mapper.sql:
 SELECT t1.`ACTIVATEDCOUNT`, t1.`ACTIVATEDDATE`, t1.`ASSIGNEDDATE`, t1.`ASSIGNEDTO`, t1.`BRANCH`, t61.`NAME` AS `BRANCHNAME`, t1.`BROWSER`, t1.`CASE`, t71.`TITLE` AS `CASENAME`, t1.`CASEVERSION`, t1.`CLOSEDBY`, t1.`CLOSEDDATE`, t1.`COLOR`, t1.`CONFIRMED`, t1.`DEADLINE`, t1.`DELETED`, t1.`DUPLICATEBUG`, t1.`ENTRY`, t1.`FOUND`, t1.`HARDWARE`, t1.`ID`, 0 AS `ISFAVORITES`, t1.`KEYWORDS`, t1.`LASTEDITEDBY`, t1.`LASTEDITEDDATE`, t1.`LINES`, t1.`LINKBUG`, t1.`MAILTO`, t1.`MODULE`, t51.`NAME` AS `MODULENAME`, (case when t1.module = '0' then '/' else (SELECT GROUP_CONCAT( tt.NAME SEPARATOR '>' )  FROM zt_module tt WHERE FIND_IN_SET( tt.id, t51.path ) AND tt.type = 'story'  GROUP BY tt.root limit 0,1) end) AS `MODULENAME1`, t1.`OPENEDBUILD`, t1.`OPENEDBY`, t1.`OPENEDDATE`, t1.`OS`, (case when t1.DEADLINE = '0000-00-00' then 0 else datediff(t1.deadline, now() ) end) AS `OVERDUEBUGS`, t1.`PLAN`, t1.`PRI`, t1.`PRODUCT`, t11.`NAME` AS `PRODUCTNAME`, t1.`PROJECT`, t21.`NAME` AS `PROJECTNAME`, t1.`REPO`, t1.`REPOTYPE`, t1.`RESOLUTION`, t1.`RESOLVEDBUILD`, t1.`RESOLVEDBY`, t1.`RESOLVEDDATE`, t1.`RESULT`, t1.`SEVERITY`, t1.`STATUS`, t1.`STORY`, t31.`TITLE` AS `STORYNAME`, t1.`STORYVERSION`, t1.`SUBSTATUS`, t1.`TASK`, t41.`NAME` AS `TASKNAME`, t1.`TESTTASK`, t1.`TITLE`, t1.`TOSTORY`, t1.`TOTASK`, t1.`TYPE`, t1.`V1`, t1.`V2` FROM `zt_bug` t1  LEFT JOIN zt_product t11 ON t1.PRODUCT = t11.ID  LEFT JOIN zt_project t21 ON t1.PROJECT = t21.ID  LEFT JOIN zt_story t31 ON t1.STORY = t31.ID  LEFT JOIN zt_task t41 ON t1.TASK = t41.ID  LEFT JOIN zt_module t51 ON t1.MODULE = t51.ID  LEFT JOIN zt_branch t61 ON t1.BRANCH = t61.ID  LEFT JOIN zt_case t71 ON t1.`CASE` = t71.ID  
			 WHERE 	t1.DELETED = '0'
				 AND t1.plan <> #{srf.webcontext.plan}
				 AND t1.product = #{srf.webcontext.product}
				 AND t1.`status` = 'active'

 SELECT 	t1.id, 	t1.product, 	t1.branch, 	t1.module, 	t1.project, 	t1.title,         t1.task, 	t1.keywords, 	t1.pri, 	t1.type, 	t1.status, 	t1.subStatus, 	t1.mailto, 	t1.openedBy, 	t1.openedDate, 	t1.assignedTo, 	t1.assignedDate, 	t1.resolvedBy, 	t1.resolvedDate, 	t1.lastEditedBy, 	t1.lastEditedDate, 	t31.`NAME` AS `PRODOCTNAME`, 	t51.`NAME` AS `PROJECTNAME`, 	( SELECT 	( CASE WHEN COUNT( t.IBZ_FAVORITESID ) > 0 THEN 1 ELSE 0 END ) AS ISFAVORITES  FROM 	T_IBZ_FAVORITES t  WHERE 	t.TYPE = 'task'  	AND t.ACCOUNT = #{srf.sessioncontext.srfloginname}  	AND t.OBJECTID = t1.id  	) AS `ISFAVORITES` 	 FROM 	zt_bug t1	         LEFT JOIN zt_module t11 ON t1.MODULE = t11.ID 	LEFT JOIN zt_product t31 ON t1.PRODUCT = t31.ID 	LEFT JOIN zt_branch t41 ON t1.BRANCH = t41.ID  	LEFT JOIN zt_project t51 ON t1.PROJECT = t51.ID   	 
			 WHERE 	t1.deleted='0'  
				 AND t1.task = #{srf.datacontext.srfparentkey}
